import React,{useState} from 'react'

const ColorPicker = () => {
    const [color, setColor] = useState('');

        return (
            <div className="container-fluid">
                <label for="exampleColorInput" className="form-label">Choose you color:</label>
                <input type="color" value={color} className="form-control form-control-color" id="exampleColorInput" title="Choose your color"></input>
                <ColorPicker width={456} height={228}
                    color={color}
                    onChange={setColor} hideHSV dark />;
            </div>
    ) 
}

export default ColorPicker
