import React,{useState} from 'react'

const ColorPicker = () => {
    const [color, setColor] = useState('');
    function handleChange(e){
        setColor(e.target.value);
    }
        return (
            <div className="container-fluid mb-3 g-3" style={{backgroundColor: `${color}`}}>
                <div>
                    <label for="exampleColorInput" className="form-label">Color picker</label>
                    <div className="row">
                        <div>
                        <input type="color" value={color} onChange={handleChange} className="form-control form-control-color col-lg-8" id="exampleColorInput" title="Choose your color"/>
                        </div>
                        <div></div>
                        <input className="form-control col" value={color} onChange={handleChange} id="disabledInput" type="text" placeholder="color seleccionado" disabled/>
                    </div>
                </div>
            </div>

    ) 
}

export default ColorPicker
