import React,{useState} from 'react'

const ColorPicker = () => {
    const [color, setColor] = useState('');
    function handleChange(e){
        setColor(e.target.value);
    }

        return (
            <div className="container-fluid">
                <div>
                <h2>Color Picker</h2>
                <label for="exampleColorInput" className="form-label">Choose you color:</label>
                <input type="color" value={color} onChange={handleChange} className="form-control form-control-color" id="exampleColorInput" title="Choose your color"/>
                <input class="form-control" value={color} onChange={handleChange} id="disabledInput" type="text" placeholder="Disabled input here..." disabled/>
                </div>
            </div>
    ) 
}

export default ColorPicker
