import React,{useState} from 'react'
import './ColorPicker.css'

const ColorPicker = () => {
    const [color, setColor] = useState('');
    function handleChange(e){
        setColor(e.target.value);
    }
        return (
            <div className="container-fluid g-3" style={{backgroundColor: `${color}`}}>
                <div className="">
                    <h2 className="form-label">Color picker</h2>
                    <div className="row ustify-content-md-center">
                        <div className="col col-lg-2">
                            <input className="form-control form-control-color" type="color" value={color} onChange={handleChange} id="exampleColorInput" title="Choose your color"/>
                        </div>
                        <div className="col-sm-3">
                            <input className="form-control" value={color} onChange={handleChange} id="disabledInput" type="text" placeholder="color seleccionado" disabled/>
                        </div>
                    </div>
                </div>
            </div>


    ) 
}

export default ColorPicker
