import React,{useState} from 'react'
import { ColorPicker, useColor } from 'react-color-palette';

const ColorPickertp = () => {
    const [color, setColor] = useState("#fff");
        return (
            <div>
                <label for="exampleColorInput" className="form-label">Choose you color:</label>
                <input type="color" className="form-control form-control-color" id="exampleColorInput" value></input>
                <ColorPicker width={456} height={228}
                    color={color}
                    onChange={setColor} hideHSV dark />;
            </div>
    ) 
}

export default ColorPickertp
