const ColorPicker = () => {
    const [color, setColor] = useState('');
    function handleChange(e){
        setColor(e.target.value);
    }
        return (
            <div className="container-fluid">
                <label for="exampleColorInput" className="form-label">Choose you color:</label>
                <input type="color" value={color} onclassName="form-control form-control-color" id="exampleColorInput" title="Choose your color"></input>

            </div>
    ) 
}

export default ColorPicker
