import React,{useState} from 'react'
import './ColorPicker.css'

const ColorPicker = () => {
    const [color, setColor] = useState('');
    function handleChange(e){
        setColor(e.target.value);
    }
        return (
        <div className="background" style={{backgroundColor: `${color}`}}>
            <div className="container-fluid">
                <div className="container">
                    <h2 className="form-label my-4">Color picker</h2>
                    <div className="inputs mb-4">
                        <input className="form-control " type="color" value={color} onChange={handleChange} id="exampleColorInput" title="Choose your color"/>
                        <input className="form-control color-selected" value={color} onChange={handleChange} id="disabledInput" type="text" placeholder="color seleccionado" disabled/>
                    </div>
                </div>
            </div>
        </div>
    ) 
}

export default ColorPicker
