import React,{useState} from 'react'

const ColorPicker = () => {
    const [color, setColor] = useState('');
    function handleChange(e){
        setColor(e.target.value);
    }
        return (
            <div className="container-fluid mb-3" style={{backgroundColor}}>
                <label for="exampleColorInput" className="form-label">Color picker</label>
                <input type="color" value={color} onChange={handleChange} className="form-control form-control-color" id="exampleColorInput" title="Choose your color"/>
                <input className="form-control" value={color} onChange={handleChange} id="disabledInput" type="text" placeholder="Disabled input here..." disabled/>
            </div>
    ) 
}

export default ColorPicker
